    /* Public methods */
    // Set a candidate solution as a byte array
    public static void setSolution(byte[] newSolution) {
        solution = newSolution;
    }

    // To make it easier we can use this method to set our candidate solution 
    // with string of 0s and 1s
    static void setSolution(String newSolution) {
        solution = new byte[newSolution.length()];
        // Loop through each character of our string and save it in our byte 
        // array
        for (int i = 0; i < newSolution.length(); i++) {
            String character = newSolution.substring(i, i + 1);
            if (character.contains("0") || character.contains("1")) {
                solution[i] = Byte.parseByte(character);
            } else {
                solution[i] = 0;
            }
        }
    }

    static int getFitness(Individual individual) {
        int fitness = 0;
//        // Loop through our individuals genes and compare them to our cadidates
//        for (int i = 0; i < individual.size() && i < solution.length; i++) {
//            if (individual.getGene(i) == solution[i]) {
//                fitness++;
//            }
//        }
//        return fitness;
        double[] cromosoma = new double[individual.size()];
        int elementsincluster = 0;
        for (int i = 0; i < individual.size(); i++) {
            cromosoma[i] = individual.getGene(i);
        }

        EuclideanDistance euclideandistance = new EuclideanDistance();
        for (int i = 0; i < data.clusters.length; i++) {
            if (euclideandistance.compute(data.clusters[i], cromosoma) == 0) {
                elementsincluster++;
                //System.out.println("Elementos en el cluster:  " + elementsincluster );
            }
        }
        fitness = elementsincluster;

        return fitness;

    }

    // Get optimum fitness
    static int getMaxFitness() {
        int maxFitness = solution.length;
        return maxFitness;
    }
